const { SMTPClient } = require('smtp-client');

function test(hook_url) { // eslint-disable-line camelcase
  return !!process.env.SMTP
          && !!process.env.SMTP_FROM
          && hook_url // eslint-disable-line camelcase
          && (hook_url.startsWith('mailto:')
            || hook_url.startsWith('mail:')
            || hook_url.startsWith('email:'));
}

function formatMime(text, html) {
  let lines = 'MIME-Version: 1.0\n';
  lines += 'Content-Type: multipart/alternative; boundary="outer-boundary"\n';
  lines += '\n\n--outer-boundary\n';
  lines += 'Content-Type: text/plain; charset="utf8"\n';
  lines += 'Content-Disposition: inline\n\n';
  lines += text;
  lines += '\n\n--outer-boundary\n';
  lines += 'Content-Type: text/html; charset="utf8"\n';
  lines += 'Content-Disposition: inline\n\n';
  lines += html;
  lines += '\n\n--outer-boundary--\n';
  return lines;
}

async function fire(id, hook_url, type = '', incoming = {}) { // eslint-disable-line camelcase
  if (process.env.SMTP && process.env.SMTP_FROM) {
    const conn = new URL(process.env.SMTP);
    const to = new URL(hook_url);
    const smtp = new SMTPClient({
      host: conn.hostname,
      port: conn.port || 25,
      secure: conn.protocol.startsWith('smtps'),
      timeout: 3000,
    });
    await smtp.connect({ timeout: 3000 });
    await smtp.greet({ hostname: conn.hostname });
    if (conn.protocol.includes('+plain')) {
      await smtp.authPlain({ username: conn.username, password: conn.password });
    } else if (conn.protocol.includes('+login')) {
      await smtp.authPlain({ username: conn.username, password: conn.password });
    }
    await smtp.mail({ from: process.env.SMTP_FROM });
    await smtp.rcpt({ to: to.pathname });
    let lines = `From: ${process.env.SMTP_FROM || to.pathname}\n`;
    lines += `To: ${to.pathname}\n`;
    lines += `Date: ${(new Date()).toString()}\n`;
    lines += `Subject: ${to.searchParams.get('subject') || incoming.subject || process.env.SMTP_SUBJECT || `Event ${type} fired.`}\n`;
    if (incoming.text && incoming.html) {
      lines += formatMime(incoming.text, incoming.html);
    } else {
      const body = `${typeof incoming === 'string' ? incoming : JSON.stringify(incoming, null, 2)}`;
      if (process.env[`SMTP_HTML_TEMPLATE_${type.replace('-', '_').toUpperCase()}`]) {
        let template = process.env[`SMTP_HTML_TEMPLATE_${type.replace('-', '_').toUpperCase()}`];
        Object.keys(incoming).filter((key) => !!key).forEach((key) => {
          template = template.replace(new RegExp(`{${key}}`, 'g'), incoming[key]);
        });
        lines += formatMime(body, template);
      } else if (process.env.SMTP_HTML_TEMPLATE) {
        lines += formatMime(body, process.env.SMTP_HTML_TEMPLATE.replace('{body}', body));
      } else {
        lines += formatMime(body, `<pre><code>${body}</code></pre>`);
      }
    }
    await smtp.data(lines);
    await smtp.quit();
    return {
      sent_metadata: '', sent_data: lines, received_code: '', received_metadata: '', received_data: '',
    };
  }
  return {};
}

module.exports = {
  test,
  fire,
  enabled: () => (!process.env.WEBHOOK_DISABLE_EMAIL),
  name: 'email',
  description: 'send an email.',
  format: 'mailto:john.smith@example.com',
  image: 'data:image/png;base64,',
};
